-- Creating a table to record the orders:
CREATE TABLE sales_order (
	customer_id INT REFERENCES customer(id),
	sales_person_id INT REFERENCES sales_person(id),
	time_order_taken TIMESTAMP NOT NULL,
	purchase_order_num BIGINT NOT NULL,
	credit_card_holder VARCHAR(100) NOT NULL,
	credit_card_num VARCHAR(16) NOT NULL,
	credit_card_exp_year SMALLINT NOT NULL,
	credit_card_exp_month SMALLINT NOT NULL,
	credit_card_code SMALLINT NOT NULL,
	id SERIAL PRIMARY KEY
);

